mapper.select:
  select a.* from y23_goods_collect a
	where a.is_delete=0 
  and a.id = #{id}

  select count(1) budget from y23_goods_collect a
	where a.is_delete=0 
	
	
	and a.userId = #{userId}
	
	
	
	and a.goodsId = #{goodsId}
	
	
	
	and date_format(a.create_time,'%Y-%m-%d') >= #{startTime}
	
	
	and date_format(a.create_time,'%Y-%m-%d') <= #{endTime}

select a.* from y23_goods_collect a
where a.is_delete=0 


and a.userId = #{userId}



and a.goodsId = #{goodsId}



and date_format(a.create_time,'%Y-%m-%d') >= #{startTime}


and date_format(a.create_time,'%Y-%m-%d') <= #{endTime}



order by ${orderBy}


limit #{page}, #{pageSize}
