mapper.select:
  select count(1) budget from y23_goods_property a 
  left join y23_property_name b on b.id = a.prop_name_id
  left join y23_property_value c on c.id = a.prop_value_id
  where a.is_delete=0
 
	and a.goods_id = #{goodsId}
	
	
	and a.prop_name_id = #{propNameId}
	
	
	and a.prop_value_id = #{propValueId}
	
	
	and b.is_key = #{isKey}
	
	
	and b.status = #{status}
	
	
	and c.status = #{status}
	
	
	and a.goods_id in (${goodsIds})

	select a.*,
		b.title as nameTitle,b.enTitle as enNameTitle,b.is_color as isColor,b.is_key as isKey, b.is_sale as isSale,b.is_must as isMust,
		c.title as valueTitle,c.enTitle as enValueTitle,c.value as value
		from y23_goods_property a 
	left join y23_property_name b on b.id = a.prop_name_id
  left join y23_property_value c on c.id = a.prop_value_id
	where a.is_delete=0
	
	and a.goods_id = #{goodsId}
	
	
	and a.prop_name_id = #{propNameId}
	
	
	and a.prop_value_id = #{propValueId}
	
	
	
	and b.is_key = #{isKey}
	
	
	and b.status = #{status}
	
	
	and c.status = #{status}
	
	
	and a.goods_id in (${goodsIds})
	
	
	order by ${orderBy}
	
	
	limit #{page}, #{pageSize}
