mapper.select:
  select a.* from y23_user a
	where a.is_delete=0 
  and a.id = #{id}

    select count(1) budget from y23_user a
  	where a.is_delete=0 
  	
  	
  	and a.nickName like '%${nickName}%'
  	
  	
  	
  	and a.name like '%${name}%'
  	
  	
  	
  	and a.phone like '%${phone}%'
  	
  	
  	
  	and a.openId = #{openId}
  	
  	
  	
  	and a.username = #{username}
  	
  	
    
  	and a.pwd = #{pwd}
  	
  	
  	
  	and a.fxname like '%${fxname}%'
  	
  	
  	
  	and a.fxphone like '%${fxphone}%'
  	
  	
  	
  	and a.status in (${statuskey})
  	
  	
  	
  	and a.status = #{status}
  	
  	
  	
  	and a.refereeId = #{refereeId}
  	
  	
  	and a.refereeId in (select b.id from y23_user b where b.is_delete= 0 and b.refereeId=#{refereeId})
  	
  	
  	and a.refereeId in (select b.id from y23_user b where b.is_delete= 0 and b.refereeId in (select c.id from y23_user c where c.is_delete= 0 and c.refereeId=#{refereeId}))
  	
  	
  	
    and a.yqcode = #{yqcode}
    
    
    
    and a.byqcode = #{byqcode}
    
    
    
    and a.id in (
    	select id from (
			select t1.id,
				if(find_in_set(refereeId, @pids) > 0, @pids := concat(@pids, ',', id), 0) as ischild
				from (
				select id,refereeId from y23_user t order by refereeId, id
				) t1,
				(select @pids := #{rootId}) t2) t3 where ischild != 0
    ),
    
    
    
  	and a.isAdmin = #{isAdmin}
  	
    
    
  	
  	and date_format(a.create_time,'%Y-%m-%d') >= #{startTime}
  	
  	
  	and date_format(a.create_time,'%Y-%m-%d') <= #{endTime}

  	select a.* from y23_user a
  	where a.is_delete=0 
  	
  	
  	and a.nickName like '%${nickName}%'
  	
  	
  	
  	and a.name like '%${name}%'
  	
  	
  	
  	and a.phone like '%${phone}%'
  	
  	
  	
  	and a.openId = #{openId}
  	
  	
  	
  	and a.username = #{username}
  	
  	
    
  	and a.pwd = #{pwd}
  	
  	
  	
  	and a.fxname like '%${fxname}%'
  	
  	
  	
  	and a.fxphone like '%${fxphone}%'
  	
  	
  	
  	and a.status in (${statuskey})
  	
  	
  	
  	and a.status = #{status}
  	
  	
  	
  	and a.refereeId = #{refereeId}
  	
  	
  	and a.refereeId in (select b.id from y23_user b where b.is_delete= 0 and b.refereeId=#{refereeId})
  	
  	
  	and a.refereeId in (select b.id from y23_user b where b.is_delete= 0 and b.refereeId in (select c.id from y23_user c where c.is_delete= 0 and c.refereeId=#{refereeId}))
  	
  	
  	
    and a.yqcode = #{yqcode}
    
    
    
    and a.byqcode = #{byqcode}
    
    
    
    and a.id in (
    	select t3.id from (
			select t1.id,
				if(find_in_set(refereeId, @pids) > 0, @pids := concat(@pids, ',', id), 0) as ischild
				from (
				select id,refereeId from y23_user t order by refereeId, id
				) t1,
				(select @pids := #{rootId}) t2) t3 where t3.ischild != 0
    )
    
    
    and a.id in (
    	SELECT T1._id id
			FROM (SELECT @r AS _id,
				(SELECT @r := refereeId FROM y23_user WHERE id = _id) AS refereeId,
				@l := @l + 1 AS lvl FROM (SELECT @r := #{childId}, @l := 0) vars,
				y23_user h WHERE @r != '0') T1
    )
    
    
    
  	and a.isAdmin = #{isAdmin}
  	
  	
  	
  	and date_format(a.create_time,'%Y-%m-%d') >= #{startTime}
  	
  	
  	and date_format(a.create_time,'%Y-%m-%d') <= #{endTime}
  	
  	
  	
  	order by ${orderBy}
  	
  	
  	limit #{page}, #{pageSize}
